mapper.update:
UPDATE store
rotationId = #{rotationId}, sidoCodeId = #{sidoCodeId}, storeName = #{storeName}, address = #{address}, tel = #{tel}, description = #{description}, directionGuide = #{directionGuide}, maxPeople = #{maxPeople}, maxPeoplePerReserve = #{maxPeoplePerReserve}, isBlocked = #{isBlocked}, locationLatX = #{locationLatX}, locationLonY = #{locationLonY}
WHERE storeId = #{storeId}

UPDATE schedule

	dayCodeId = #{dayCodeId},
	open = #{open},
	close = #{close},
	closeDay = #{closeDay}

WHERE scheduleId = #{scheduleId}

mapper.delete:
DELETE FROM storeImages
WHERE storeId = #{storeId}
AND fileName IN

	#{fileName}

DELETE FROM storeCategory
WHERE storeId = #{storeId}
AND categoryCodeId = #{categoryCodeId}

DELETE FROM facility
WHERE facilityCode = #{facilityCode}
AND storeId = #{storeId}

mapper.select:
SELECT * 
FROM schedule
WHERE dayCodeId = #{dayCode}
AND storeId = #{storeId}
ORDER BY storeId

SELECT * 
FROM reserveSlots
WHERE DATE(slotDatetime) = #{date}
AND storeId = #{storeId}

mapper.delete:
DELETE FROM reserveSlots
WHERE storeId = #{storeId}
  AND DAYOFWEEK(slotDatetime) - 1 = #{dayCodeId}
