mapper.insert:
		INSERT INTO user(email, password, name, mobile, gender
    	, profileImageUrl
    	, profileImageName
    	)
    	VALUES(#{email}, SHA2(#{password}, 256), #{name}, #{mobile}, #{gender}
    	, #{profileImageUrl}
    	, #{profileImageName}
    	)

mapper.update:
		UPDATE user SET name = #{name},  mobile = #{mobile},
		profileImageUrl=#{profileImageUrl}, profileImageName =#{profileImageName}
    	WHERE userId = #{userId}
