mapper.select:
select * from reserve where storeId = #{storeId}


		order by reserveTime desc


		order by reserveTime asc

mapper.update:
update notification 
set isRead = true 
where alarmId = #{alarmId}

		 update reserveSlots 
 		 set reserved = false 
 		 where slotDatetime = #{reserveTime} and storeId = #{storeId}
